// card-highlight 1.1.1 6/1/2021 11:13:01 PM
class t extends HTMLElement {
    renderParagraph() {
        const t = this.getAttribute("description") || null;
        return t ? `<n-paragraph size="2" size_mobile="3">${t}</n-paragraph>` : ""
    }
    renderCta() {
        const t = this.getAttribute("cta_text") || null;
        if (!t) return "";
        const i = this.getAttribute("cta_link") || null,
            n = this.getAttribute("cta_style") || null;
        return `<n-cta\n                    class="c-CardHighlight_cta"\n                    campaign_id="${this.getAttribute("campaign_id")}"\n                    campaign_name="${this.getAttribute("campaign_name")}"\n                    campaign_position="${this.getAttribute("campaign_position")}"\n                    campaign_creative="${this.getAttribute("campaign_creative")}"\n                       class="c-Card_cta"\n                       link="${i}"\n                       look="${n}"\n                       size="4"\n               >${t}</n-cta>`
    }
    renderParagraphPopin() {
        const t = this.getAttribute("popin_text") || null,
            i = this.getAttribute("base_line") || null;
        return !i || t ? "" : `<n-paragraph size="4" size_mobile="3">${i}</n-paragraph>`
    }
    renderCtaPopin() {
        const t = this.getAttribute("popin_text") || null;
        if (!t) return "";
        const i = this.getAttribute("popin_id") || null;
        return `<n-cta\n                    class="c-CardHighlight_PopinCta"\n                    campaign_id="${this.getAttribute("campaign_id")}"\n                    campaign_name="${this.getAttribute("campaign_name")}"\n                    campaign_position="${this.getAttribute("campaign_position")}"\n                    campaign_creative="${this.getAttribute("campaign_creative")}"\n                    look="link-underline"\n                    size="4"\n                    popin_id="${i}"\n               >${t}</n-cta>`
    }
    render() {
        this.classList.add("c-CardHighlight"), this.innerHTML = `<n-zone\n                  campaign_id="${this.getAttribute("campaign_id")}"\n                  campaign_name="${this.getAttribute("campaign_name")}"\n                  campaign_position="${this.getAttribute("campaign_position")}"\n                  campaign_creative="${this.getAttribute("campaign_creative")}"\n                  background_mobile="${this.getAttribute("background_mobile")}"\n                  background_desktop="${this.getAttribute("background_desktop")}"\n                  contrast="${this.getAttribute("contrast")}"\n                  auto_height="true"\n                  align="top">\n                   <n-heading level="2">${this.getAttribute("heading")}</n-heading>\n                   ${this.renderParagraph()}\n                   ${this.renderCta()}\n                   ${this.renderParagraphPopin()}\n                   ${this.renderCtaPopin()}\n        </n-zone>`
    }
    connectedCallback() {
        const t = window.innerWidth;
        this.alignMobile = this.getAttribute("align_mobile") || "left", this.isMobile = t < 750, this.render()
    }
}
customElements.get("n-card-highlight") || customElements.define("n-card-highlight", t);
export default t;