class t extends HTMLElement {
    renderParagraph() {
        const t = this.getAttribute("description") || null;
        return this.isMobile || !t ? "" : `<n-paragraph size="3">${t}</n-paragraph>`
    }
    renderCta() {
        const t = this.getAttribute("cta_text") || null;
        if (!t) return "";
        const n = this.getAttribute("cta_link") || null,
            i = this.getAttribute("cta_style") || null;
        return `<n-cta\n                      class="c-Card_cta"\n                      campaign_id="${this.getAttribute("campaign_id")}"\n                      campaign_name="${this.getAttribute("campaign_name")}"\n                      campaign_position="${this.getAttribute("campaign_position")}"\n                      campaign_creative="${this.getAttribute("campaign_creative")}"\n                      link="${n}"\n                      look="${i}"\n                      size="4"\n               >${t}</n-cta>`
    }
    renderCtaPopin() {
        const t = this.getAttribute("popin_text") || null,
            n = this.getAttribute("popin_id") || null;
        return t && n ? `\n            <n-cta\n                class="c-Card-Cta-PopinCta"\n                look="link-underline"\n                size="4"\n                popin_id="${n}"\n                >${t}\n            </n-cta>` : ""
    }
    render() {
        this.classList.add("c-Card"), this.getClassNames().map(t => {
            this.classList.add(t)
        });
        const t = this.getAttribute("heading") || "";
        this.innerHTML = `<n-zone\n                  campaign_id="${this.getAttribute("campaign_id")}"\n                  campaign_name="${this.getAttribute("campaign_name")}"\n                  campaign_position="${this.getAttribute("campaign_position")}"\n                  campaign_creative="${this.getAttribute("campaign_creative")}"\n                  background_mobile="${this.getAttribute("background_mobile")}"\n                  background_desktop="${this.getAttribute("background_desktop")}"\n                  contrast="${this.getAttribute("contrast")}"\n                  auto_height="true"\n                  align="${this.isMobile?this.alignMobile:"top"}">\n               <n-heading level="2">${t}</n-heading>\n               ${this.renderParagraph()}\n               ${this.renderCta()}\n               ${this.renderCtaPopin()}\n        </n-zone>`
    }
    connectedCallback() {
        const t = window.innerWidth;
        this.alignMobile = this.getAttribute("align_mobile") || "left", this.isMobile = t < 750, this.render()
    }
    getClassNames() {
        const t = [];
        return t.push("c-Card--" + this.alignMobile), t
    }
}
customElements.get("n-card") || customElements.define("n-card", t);
export default t;