// product-column-item 1.1.1 6/1/2021 11:13:01 PM
class t extends HTMLElement {
    render() {
        const t = this.getAttribute("name"),
            e = this.getAttribute("image"),
            n = this.getAttribute("product_type") || "",
            i = n ? "c-ProductColumnItem__" + n : "";
        this.innerHTML = `<div class="c-ProductColumnItem ${i}"\n        data-product-item-id="${this.sku}"\n        data-product-section="${this.getAttribute("tracking_component_name")||""}"\n        data-product-position="${parseInt(this.getAttribute("position")||0,10)+1}">\n        <div class="c-ProductColumnItem__Content_Reflect">\n            <div class="c-ProductColumnItem__Image_Reflect">\n                <img\n                    loading=lazy\n                    class="c-ProductColumnItem__Visual"\n                    alt="${t}"\n                    title="${t}"\n                    src="${e}">\n                    ${this.reflectImage(e)}\n            </div>\n            <n-heading level="5">\n                ${t}\n            </n-heading>\n        </div>\n            <div class="c-ProductColumnItem__AddToCart">\n                <n-add-to-cart sku="${this.sku}"></n-add-to-cart>\n            </div>\n        </div>`
    }
    reflectImage(t) {
        return "true" === this.getAttribute("capsule_reflect") ? `<span class="c-ProductColumnItem__Visual_Reflect">\n                <img loading=lazy src="${t}" alt="" title="" />\n            </span>` : ""
    }
    connectedCallback() {
        this.id = "n-product-" + this.getAttribute("sku"), this.sku = this.getAttribute("sku"), this.render()
    }
    disconnectedCallback() {}
}
customElements.get("n-product-column-item") || customElements.define("n-product-column-item", t);
export default t;